import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styling/recommendations.css';

const Recommendations = () => {
  const { mood } = useParams(); // <-- Grab the mood from URL
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Fallback image to use if a cover doesn't load
  const fallbackImage = "https://i.scdn.co/image/ab67616d0000b273000000000000000000000000";

  // Hardcoded mood-based song recommendations with images that should work
  const moodSongs = {
    happy: [
      { id: 1, title: "Don't Stop Me Now", artist: "Queen", duration: "3:29", cover: "https://i.scdn.co/image/ab67616d0000b2738485fe7e852d9d47b0ed9e44", preview: "" },
      { id: 2, title: "Happy", artist: "Pharrell Williams", duration: "3:53", cover: "https://i.scdn.co/image/ab67616d0000b273e75184a0a1122ff13c51ccc3", preview: "" },
      { id: 3, title: "Walking on Sunshine", artist: "Katrina and The Waves", duration: "3:43", cover: "https://i.scdn.co/image/ab67616d0000b273a72f316c0984e6b7812463a4", preview: "" }
    ],
    sad: [
      { id: 4, title: "Someone Like You", artist: "Adele", duration: "4:45", cover: "https://i.scdn.co/image/ab67616d0000b273292d0fe7d42bf6bfef3da3c5", preview: "" },
      { id: 5, title: "Hurt", artist: "Johnny Cash", duration: "3:38", cover: "https://i.scdn.co/image/ab67616d0000b2732fe8e9c5ef4fad06894bd359", preview: "" },
      { id: 6, title: "Everybody Hurts", artist: "R.E.M.", duration: "5:20", cover: "https://i.scdn.co/image/ab67616d0000b2737d664560be0e574ee7f53d0c", preview: "" }
    ],
    energetic: [
      { id: 7, title: "Eye of the Tiger", artist: "Survivor", duration: "4:05", cover: "https://i.scdn.co/image/ab67616d0000b273b1e0199471707c0f12ce64a9", preview: "" },
      { id: 8, title: "Thunderstruck", artist: "AC/DC", duration: "4:52", cover: "https://i.scdn.co/image/ab67616d0000b273c4f52bf28232fce4d76d6d7e", preview: "" },
      { id: 9, title: "Lose Yourself", artist: "Eminem", duration: "5:26", cover: "https://i.scdn.co/image/ab67616d0000b273b6fe3f95aa11ab7c98df4dca", preview: "" }
    ],
    calm: [
      { id: 10, title: "Weightless", artist: "Marconi Union", duration: "8:59", cover: "https://i.scdn.co/image/ab67616d0000b273cb48d590282ea187cc498839", preview: "" },
      { id: 11, title: "Clair de Lune", artist: "Claude Debussy", duration: "5:04", cover: "https://i.scdn.co/image/ab67616d0000b273f5e8bb5d5c12da170b6c55da", preview: "" },
      { id: 12, title: "Ocean Eyes", artist: "Billie Eilish", duration: "3:20", cover: "https://i.scdn.co/image/ab67616d0000b273c4d43a63b487c3d2a40747d5", preview: "" }
    ],
    romantic: [
      { id: 13, title: "All of Me", artist: "John Legend", duration: "4:30", cover: "https://i.scdn.co/image/ab67616d0000b273e8eab269e7ae1763f34000be", preview: "" },
      { id: 14, title: "Perfect", artist: "Ed Sheeran", duration: "4:23", cover: "https://i.scdn.co/image/ab67616d0000b2737636e1c9e67eaafc8c7a5045", preview: "" },
      { id: 15, title: "Can't Help Falling In Love", artist: "Elvis Presley", duration: "3:00", cover: "https://i.scdn.co/image/ab67616d0000b273a1d5b5777b8e5d3f11b5d5a3", preview: "" }
    ],
    focus: [
      { id: 16, title: "Lo-Fi Beats", artist: "Chillhop Music", duration: "3:22", cover: "https://i.scdn.co/image/ab67616d0000b2734e5afb82523e816116927328", preview: "" },
      { id: 17, title: "Rain Sounds", artist: "Nature Noise", duration: "4:10", cover: "https://i.scdn.co/image/ab67616d0000b273e0c82a598675d120af028abb", preview: "" },
      { id: 18, title: "Brain Food", artist: "Spotify", duration: "3:40", cover: "https://i.scdn.co/image/ab67706f00000002d49add4e99fa650a6521742d", preview: "" }
    ]
  };

  useEffect(() => {
    const lowerMood = mood?.toLowerCase();
    const moodData = moodSongs[lowerMood];

    if (moodData) {
      setRecommendations(moodData);
    } else {
      setRecommendations([]);
    }
    
    // Add a slight delay before removing loading state
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  }, [mood]);

  const handleBack = () => {
    navigate('/dashboard');
  };

  // Handle image error and replace with fallback
  const handleImageError = (e) => {
    e.target.src = fallbackImage;
  };

  return (
    <div className="recommendations-page">
      <div className="recommendations-header">
        <h1>{mood ? `${mood.charAt(0).toUpperCase() + mood.slice(1)} Vibes` : "Mood Vibes"}</h1>
        <button onClick={handleBack}>← Back</button>
      </div>

      {isLoading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading recommendations...</p>
        </div>
      ) : (
        <div className="recommendations-list">
          {recommendations.length > 0 ? (
            recommendations.map((song) => (
              <div key={song.id} className="song-card">
                <div className="image-container">
                  <img 
                    src={song.cover} 
                    alt={`${song.title} by ${song.artist}`} 
                    onError={handleImageError}
                    loading="lazy"
                  />
                  <div className="image-loading-overlay">
                    <div className="spinner-small"></div>
                  </div>
                </div>
                <div className="play-button">
                  <span className="play-icon">▶</span>
                </div>
                <div className="song-info">
                  <h3>{song.title}</h3>
                  <p>{song.artist}</p>
                  <span>{song.duration}</span>
                </div>
              </div>
            ))
          ) : (
            <div className="no-results">
              <p>No songs found for this mood.</p>
              <button className="try-again-button" onClick={handleBack}>Try Another Mood</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Recommendations;
