import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import { getTopSongs } from '../services/deezerApi';

const TopSongs = () => {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTopSongs = async () => {
      const topSongs = await getTopSongs();
      setSongs(topSongs);
      setLoading(false);
    };
    
    fetchTopSongs();
  }, []);

  return (
    <Layout title="Deezer Top Songs" showNav={true}>
      {loading ? (
        <div style={localStyles.loading}>
          <div style={localStyles.spinner}></div>
          <p>Loading top songs from Deezer...</p>
        </div>
      ) : (
        <div style={localStyles.songsContainer}>
          {songs.map((song, index) => (
            <div key={song.id} style={localStyles.songCard}>
              <div style={localStyles.songRank}>{index + 1}</div>
              <img 
                src={song.cover} 
                alt={`${song.title} cover`} 
                style={localStyles.coverArt}
              />
              <div style={localStyles.songInfo}>
                <h3 style={localStyles.songTitle}>{song.title}</h3>
                <p style={localStyles.songArtist}>{song.artist}</p>
                <p style={localStyles.songAlbum}>{song.album}</p>
              </div>
              <div style={localStyles.songDuration}>{song.duration}</div>
              {song.preview && (
                <audio controls style={localStyles.audioPlayer}>
                  <source src={song.preview} type="audio/mp3" />
                </audio>
              )}
            </div>
          ))}
        </div>
      )}
    </Layout>
  );
};

const localStyles = {
  songsContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
    width: '100%',
  },
  songCard: {
    display: 'flex',
    alignItems: 'center',
    padding: '15px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '8px',
    transition: 'all 0.3s',
    ':hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.15)',
    }
  },
  coverArt: {
    width: '60px',
    height: '60px',
    borderRadius: '4px',
    marginRight: '15px',
    objectFit: 'cover',
  },
  songRank: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#8e44ad',
    minWidth: '30px',
    textAlign: 'center',
    marginRight: '15px',
  },
  songInfo: {
    flex: 1,
    minWidth: 0, // Prevent overflow
  },
  songTitle: {
    color: '#fff',
    fontSize: '16px',
    margin: '0 0 5px 0',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
  songArtist: {
    color: 'rgba(255, 255, 255, 0.8)',
    fontSize: '14px',
    margin: '0 0 3px 0',
  },
  songAlbum: {
    color: 'rgba(255, 255, 255, 0.6)',
    fontSize: '12px',
    margin: 0,
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
  songDuration: {
    color: 'rgba(255, 255, 255, 0.5)',
    fontSize: '14px',
    margin: '0 15px',
  },
  audioPlayer: {
    width: '150px',
    height: '40px',
  },
  loading: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '200px',
    color: 'rgba(255, 255, 255, 0.7)',
  },
  spinner: {
    border: '4px solid rgba(255, 255, 255, 0.1)',
    borderTop: '4px solid #8e44ad',
    borderRadius: '50%',
    width: '40px',
    height: '40px',
    animation: 'spin 1s linear infinite',
    marginBottom: '15px',
  }
};

export default TopSongs;
