import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Layout = ({ children, showNav = false }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('selectedMood');
    navigate('/login');
  };

  return (
    <div style={styles.layout}>
      {showNav && (
        <aside style={styles.sidebar}>
          <h1 style={styles.logo}>ðŸŽµ Moodify</h1>
          <nav style={styles.navLinks}>
            <Link to="/dashboard" style={styles.link}>Dashboard</Link>
            <Link to="/top-songs" style={styles.link}>Top Songs</Link>
            <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
          </nav>
        </aside>
      )}

      <main style={styles.mainContent}>
        {children}
      </main>
    </div>
  );
};

const styles = {
  layout: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#121212',
    color: 'white',
    fontFamily: 'Helvetica, Arial, sans-serif',
  },
  sidebar: {
    width: '220px',
    backgroundColor: '#000',
    padding: '20px',
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
    borderRight: '1px solid #333',
  },
  logo: {
    fontSize: '24px',
    color: '#1DB954',
    fontWeight: 'bold',
    marginBottom: '30px',
  },
  navLinks: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    transition: 'color 0.3s',
  },
  logoutButton: {
    background: 'none',
    border: '1px solid #1DB954',
    color: '#1DB954',
    padding: '8px',
    borderRadius: '6px',
    cursor: 'pointer',
    marginTop: 'auto',
  },
  mainContent: {
    flex: 1,
    padding: '40px',
    overflowY: 'auto',
  }
};

export default Layout;
