import React, { useEffect, useState } from 'react';
import Layout from '../components/layout';
import { getTopSongs } from '../services/spotifyapi';

const formatDuration = (seconds) => {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins}:${secs.toString().padStart(2, '0')}`;
};

const TopSongs = () => {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadSongs = async () => {
      try {
        const topSongs = await getTopSongs();
        setSongs(topSongs);
      } catch (err) {
        setError('Failed to load top songs. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    loadSongs();
  }, []);

  return (
    <Layout title="Spotify Top 50" showNav={true}>
      <div style={styles.container}>
        <h2 style={styles.header}>Spotify Top 50 Global</h2>
        
        {loading ? (
          <div style={styles.loading}>Loading top songs...</div>
        ) : error ? (
          <div style={styles.error}>{error}</div>
        ) : (
          <div style={styles.songList}>
            {songs.map((song) => (
              <div key={song.id} style={styles.songCard}>
                <span style={styles.rank}>{song.rank}</span>
                <img 
                  src={song.cover} 
                  alt={`${song.title} cover`} 
                  style={styles.cover}
                  onError={(e) => e.target.src = 'https://via.placeholder.com/60'}
                />
                <div style={styles.songInfo}>
                  <h3 style={styles.title}>{song.title}</h3>
                  <p style={styles.artist}>{song.artist}</p>
                </div>
                <span style={styles.duration}>
                  {formatDuration(song.duration)}
                </span>
                {song.preview && (
                  <audio 
                    controls 
                    style={styles.audioPlayer}
                    src={song.preview} 
                  />
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </Layout>
  );
};

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '20px'
  },
  header: {
    color: '#1DB954',
    marginBottom: '30px',
    textAlign: 'center'
  },
  loading: {
    textAlign: 'center',
    fontSize: '18px',
    padding: '40px'
  },
  error: {
    color: '#ff6b6b',
    textAlign: 'center',
    padding: '20px',
    fontSize: '16px'
  },
  songList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px'
  },
  songCard: {
    display: 'flex',
    alignItems: 'center',
    padding: '15px',
    backgroundColor: 'rgba(255, 255, 255, 0.05)',
    borderRadius: '8px',
    transition: 'all 0.3s ease',
    ':hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.1)'
    }
  },
  rank: {
    fontWeight: 'bold',
    color: '#1DB954',
    width: '30px',
    textAlign: 'center'
  },
  cover: {
    width: '60px',
    height: '60px',
    borderRadius: '4px',
    margin: '0 15px',
    objectFit: 'cover'
  },
  songInfo: {
    flex: 1,
    minWidth: 0
  },
  title: {
    margin: 0,
    fontSize: '16px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis'
  },
  artist: {
    margin: '5px 0 0',
    fontSize: '14px',
    color: 'rgba(255, 255, 255, 0.7)'
  },
  duration: {
    margin: '0 15px',
    color: 'rgba(255, 255, 255, 0.5)'
  },
  audioPlayer: {
    width: '150px',
    height: '40px'
  }
};

export default TopSongs;
