const clientId = process.env.REACT_APP_SPOTIFY_CLIENT_ID;
const clientSecret = process.env.REACT_APP_SPOTIFY_CLIENT_SECRET;

let accessToken = null;

// Function to get an access token
const getAccessToken = async () => {
  if (accessToken) return accessToken;

  const response = await fetch('https://accounts.spotify.com/api/token', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      Authorization: 'Basic ' + btoa(`${clientId}:${clientSecret}`)
    },
    body: 'grant_type=client_credentials'
  });

  if (!response.ok) throw new Error('Failed to get access token');

  const data = await response.json();
  accessToken = data.access_token;
  return accessToken;
};

// Function to fetch top songs from a Spotify playlist
export const getTopSongs = async () => {
  try {
    const token = await getAccessToken();
    const playlistId = '37i9dQZEVXbMDoHDwVN2tF'; // Example: Spotify Top 50 Global

    const response = await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks?limit=10`, {
      headers: { Authorization: `Bearer ${token}` }
    });

    if (!response.ok) throw new Error('Failed to fetch playlist');

    const { items } = await response.json();

    return items.map(({ track }, index) => ({
      id: track.id,
      rank: index + 1,
      title: track.name,
      artist: track.artists.map(a => a.name).join(', '),
      duration: Math.floor(track.duration_ms / 1000),
      cover: track.album.images[0]?.url || 'https://via.placeholder.com/60',
      preview: track.preview_url
    }));
  } catch (error) {
    console.error('Error fetching songs:', error);
    return [];
  }
};
