import React, { useEffect, useState } from 'react';
import Layout from '../components/layout';
import { getTopSongs } from '../services/spotifyapi';


const formatDuration = (seconds) => {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins}:${secs.toString().padStart(2, '0')}`;
};

const TopSongs = () => {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadSongs = async () => {
      try {
        const topSongs = await getTopSongs();
        setSongs(topSongs);
      } catch (err) {
        setError('Failed to load top songs');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    loadSongs();
  }, []);

  const localStyles = {
    container: {
      maxWidth: '800px',
      margin: '0 auto',
      padding: '20px'
    },
    loading: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '200px',
      fontSize: '1.2rem'
    },
    error: {
      color: '#ff6b6b',
      textAlign: 'center',
      padding: '20px'
    },
    songList: {
      display: 'flex',
      flexDirection: 'column',
      gap: '15px'
    },
    songCard: {
      display: 'flex',
      alignItems: 'center',
      padding: '15px',
      backgroundColor: 'rgba(255, 255, 255, 0.05)',
      borderRadius: '8px',
      transition: 'all 0.3s ease',
      ':hover': {
        backgroundColor: 'rgba(255, 255, 255, 0.1)'
      }
    },
    rank: {
      fontWeight: 'bold',
      color: '#1DB954',
      width: '30px',
      textAlign: 'center'
    },
    cover: {
      width: '60px',
      height: '60px',
      borderRadius: '4px',
      margin: '0 15px',
      objectFit: 'cover'
    },
    songInfo: {
      flex: 1,
      minWidth: 0
    },
    title: {
      margin: 0,
      fontSize: '16px',
      whiteSpace: 'nowrap',
      overflow: 'hidden',
      textOverflow: 'ellipsis'
    },
    artist: {
      margin: '5px 0 0',
      fontSize: '14px',
      color: 'rgba(255, 255, 255, 0.7)'
    },
    duration: {
      margin: '0 15px',
      color: 'rgba(255, 255, 255, 0.5)'
    },
    audioPlayer: {
      width: '150px',
      height: '40px'
    }
  };

  return (
    <Layout title="Spotify Top Songs" showNav={true}>
      <div style={localStyles.container}>
        <h2>Spotify Top 20 Global</h2>
        
        {loading ? (
          <div style={localStyles.loading}>Loading top songs...</div>
        ) : error ? (
          <div style={localStyles.error}>{error}</div>
        ) : (
          <div style={localStyles.songList}>
            {songs.map((song, index) => (
              <div key={song.id} style={localStyles.songCard}>
                <span style={localStyles.rank}>{index + 1}</span>
                <img 
                  src={song.cover} 
                  alt={`${song.title} cover`} 
                  style={localStyles.cover}
                />
                <div style={localStyles.songInfo}>
                  <h3 style={localStyles.title}>{song.title}</h3>
                  <p style={localStyles.artist}>{song.artist}</p>
                </div>
                <span style={localStyles.duration}>
                  {formatDuration(song.duration)}
                </span>
                {song.preview && (
                  <audio 
                    controls 
                    style={localStyles.audioPlayer}
                    src={song.preview} 
                  />
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </Layout>
  );
};

export default TopSongs;
