export const searchSongsByMood = async (mood) => {
    // Map moods to Deezer search queries
    const moodQueries = {
        happy: 'happy',
        sad: 'sad',
        angry: 'angry',
        calm: 'chill',
        tired: 'relax',
        energetic: 'energy'
    };

    const query = moodQueries[mood.toLowerCase()] || mood;
    
    try {
        const response = await fetch(`https://api.deezer.com/search?q=${query}&order=RATING_DESC&limit=10`);
        const data = await response.json();
        return data.data.map(song => ({
            id: song.id,
            title: song.title,
            artist: song.artist.name,
            duration: formatDuration(song.duration),
            cover: song.album.cover_medium,
            preview: song.preview
        }));
    } catch (error) {
        console.error('Error fetching mood-based songs:', error);
        return [];
    }
};

const formatDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
};
