import React from 'react';
import Layout from '../components/layout';

const TopSongs = () => {
  const topSongs = [
    { id: 1, title: "Blinding Lights", artist: "The Weeknd", plays: "3.2B" },
    { id: 2, title: "Shape of You", artist: "Ed Sheeran", plays: "3.1B" },
    { id: 3, title: "Dance Monkey", artist: "Tones and I", plays: "2.8B" },
    { id: 4, title: "Rockstar", artist: "Post Malone ft. 21 Savage", plays: "2.7B" },
    { id: 5, title: "One Dance", artist: "Drake ft. Wizkid & Kyla", plays: "2.6B" },
    { id: 6, title: "Closer", artist: "The Chainsmokers ft. Halsey", plays: "2.5B" },
    { id: 7, title: "Sunflower", artist: "Post Malone & Swae Lee", plays: "2.4B" },
    { id: 8, title: "Stay", artist: "The Kid LAROI & Justin Bieber", plays: "2.3B" },
    { id: 9, title: "Believer", artist: "Imagine Dragons", plays: "2.2B" },
    { id: 10, title: "Perfect", artist: "Ed Sheeran", plays: "2.1B" }
  ];

  return (
    <Layout title="Top Songs Worldwide" showNav={true}>
      <div style={localStyles.songsContainer}>
        {topSongs.map((song, index) => (
          <div key={song.id} style={localStyles.songCard}>
            <div style={localStyles.songRank}>{index + 1}</div>
            <div style={localStyles.songInfo}>
              <h3 style={localStyles.songTitle}>{song.title}</h3>
              <p style={localStyles.songArtist}>{song.artist}</p>
            </div>
            <div style={localStyles.songPlays}>{song.plays} plays</div>
          </div>
        ))}
      </div>
    </Layout>
  );
};

const localStyles = {
  songsContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    width: '100%',
  },
  songCard: {
    display: 'flex',
    alignItems: 'center',
    padding: '15px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '8px',
    transition: 'all 0.3s',
    ':hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.15)',
      transform: 'translateY(-2px)',
    }
  },
  songRank: {
    fontSize: '20px',
    fontWeight: 'bold',
    color: '#8e44ad',
    minWidth: '40px',
    textAlign: 'center',
  },
  songInfo: {
    flex: 1,
    margin: '0 15px',
  },
  songTitle: {
    color: '#fff',
    fontSize: '16px',
    margin: '0 0 5px 0',
  },
  songArtist: {
    color: 'rgba(255, 255, 255, 0.7)',
    fontSize: '14px',
    margin: 0,
  },
  songPlays: {
    color: 'rgba(255, 255, 255, 0.5)',
    fontSize: '14px',
  }
};

export default TopSongs;
